var request = require('request');
var options = {
    'method': 'GET',
    'url': 'http://localhost:8080/api/needles?api_key={}&haystack=Like%20finding%20a%20needle%20in%20a%20&needles=finding%2Cneedle%2Ca%2Chaystack%2Cneedles',
    'headers': {
    }
};
request(options, function (error, response) {
    if (error) throw new Error(error);
    console.log(response.body);
});
